import { Component, Input, Signal, computed } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ReviewsService } from '../reviews.service';
import { Review } from '../review.model';
import { MdbModalService, MdbModalRef } from 'mdb-angular-ui-kit/modal';
import { ConfirmationModalComponent } from 'src/app/confirmation-modal/confirmation-modal.component';
import { UsersService } from 'src/app/users/users.service';
import { User } from 'src/app/users/user.model';

@Component({
  selector: 'app-review-item',
  templateUrl: './review-item.component.html',
  styleUrls: ['./review-item.component.scss'],
})
export class ReviewItemComponent {
  @Input('review-details') review: Review;
  //the modal
  modalRef: MdbModalRef<ConfirmationModalComponent>;

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private reviewService: ReviewsService,
    private modalService: MdbModalService,
    private userService: UsersService
  ) {}

  deleteReview(id: string) {
    this.reviewService.deleteReview(id);
    this.router.navigateByUrl(
      `courses/${this.reviewService.courseIdSignal()}#reviews`
    );
  }

  updateReview(id: string) {
    this.router.navigateByUrl(`/reviews/new/${id}`);
  }

  //Open the modal before deleting
  //its centered
  openModal(id: string) {
    this.modalRef = this.modalService.open(ConfirmationModalComponent, {
      modalClass: 'modal-dialog-centered',
      data: {
        title: 'Please confirm deletion',
        message: 'Do you wish to proceed with deleting this review?',
        action: 'Delete',
      },
    });

    //if the deletion has been confirmed
    this.modalRef.onClose.subscribe((message) => {
      if (message === 'confirmed') {
        this.deleteReview(id);
      }
    });
  }
  //current logged in user
  currentUser: Signal<User> = computed(() => this.userService.user());

  //Check if the current logged in user
  //is the author or associated with the review
  isCurrentUserReviewAuthor(): boolean {
    if (this.review.userId['_id'] === this.currentUser()['_id']) {
      return true;
    }
    //if its the admin
    else if (this.currentUser().isAdmin) {
      return true;
    } else {
      return false;
    }
  }

  //placeholder image in case the reviewer
  //does not have a profile picture
  placeholderImageUrl: Signal<string> = computed(() =>
    this.userService.imagePlaceholderUrl(this.review.userId.name)
  );
}
<!-- For small screens start -->
<div class="card d-lg-none mb-4">
  <div class="card-body">
    <div class="d-flex flex-start align-items-center">
      <img
        class="rounded-circle shadow-1-strong me-3 reviewer-image"
        [src]="
          review.userId.imageUrl
            ? review.userId.imageUrl
            : placeholderImageUrl()
        "
        alt="avatar"
        width="75"
        height="75"
      />

      <div>
        <h6 class="fw-bold text-primary mb-1">
          {{ review.userId.name }}
        </h6>
        <div class="d-flex justify-content-start">
          <star-rating
            class="ms-n1"
            [readOnly]="true"
            [starType]="'svg'"
            [size]="'medium'"
            [rating]="review.stars"
          ></star-rating>
        </div>
        <p class="text-muted small mb-0">{{ review.updatedAt | moment }}</p>
      </div>
    </div>

    <p class="mt-2 mb-2">
      {{ review.content }}
    </p>
    <!-- show the edit and delete button if the current logged in user is the author of the review -->
    <div
      *ngIf="isCurrentUserReviewAuthor()"
      class="d-flex justify-content-between align-items-center"
    >
      <p class="small mb-0" style="color: #aaa">
        <a
          [routerLink]="'reviews/' + review['_id'] + '/edit'"
          class="link-secondary"
          fragment="reviewForm"
          >Edit</a
        >
        •
        <a (click)="openModal(review['_id'])" class="link-secondary">Remove</a>
        •
      </p>
    </div>
  </div>
</div>
<!-- For small screens end -->

<!-- For large screens start -->
<div class="d-none d-lg-flex flex-start mb-4">
  <img
    class="rounded-circle shadow-1-strong me-3 reviewer-image"
    [src]="
      review.userId.imageUrl ? review.userId.imageUrl : placeholderImageUrl()
    "
    alt="avatar"
    width="80"
    height="80"
  />

  <div class="card w-100">
    <div class="card-body p-4">
      <div class="">
        <h5 class="text-primary">
          {{ review.userId.name }}
        </h5>
        <div class="d-flex justify-content-start">
          <star-rating
            class="ms-n1"
            [readOnly]="true"
            [starType]="'svg'"
            [size]="'medium'"
            [rating]="review.stars"
          ></star-rating>
        </div>

        <p class="small text-muted">
          {{ review.updatedAt | moment }}
        </p>

        <p>{{ review.content }}</p>
        <!-- show the edit and delete button if the current logged in user is the author of the review -->
        <div
          *ngIf="isCurrentUserReviewAuthor()"
          class="d-flex justify-content-between align-items-center"
        >
          <p class="small mb-0" style="color: #aaa">
            <a
              [routerLink]="'reviews/' + review['_id'] + '/edit'"
              class="link-secondary"
              fragment="reviewForm"
              >Edit</a
            >
            •
            <a (click)="openModal(review['_id'])" class="link-secondary"
              >Remove</a
            >
            •
          </p>
        </div>
      </div>
    </div>
  </div>
</div>
<!-- For large screens end -->
