import { Component, HostListener } from '@angular/core';
import { NgxAnimatedCounterParams } from '@bugsplat/ngx-animated-counter/lib/ngx-animated-counter-params';

@Component({
  selector: 'app-homepage-statistics',
  templateUrl: './homepage-statistics.component.html',
  styleUrls: ['./homepage-statistics.component.scss'],
})
export class HomepageStatisticsComponent {
  //Parameters for animating the numbers
  params: NgxAnimatedCounterParams[] = [
    {
      start: 0,
      end: 1000,
      interval: 50,
      increment: 20,
    },
    {
      start: 0,
      end: 600,
      interval: 50,
      increment: 10,
    },
    {
      start: 0,
      end: 500,
      interval: 50,
      increment: 10,
    },
    {
      start: 0,
      end: 4,
      interval: 500,
      increment: 1,
    },
  ];

  //is the statistics part of the  homepage component active
  isPartActive: boolean = false;

  @HostListener('window:scroll', ['$event'])
  onScroll(event: Event) {
    // Calculate the scroll position
    const scrollPosition =
      window.scrollY ||
      document.documentElement.scrollTop ||
      document.body.scrollTop;

    // Set the isPartActive flag based on the scroll position
    this.isPartActive = scrollPosition > 300; // Replace 300 with the desired scroll position for activation
  }
}
<!-- Jumbotron -->
<div
  *ngIf="isPartActive"
  class="bg-image p-5 text-center shadow-1-strong w-fluid text-white mt-5"
  style="
    background-image: url('https://images.unsplash.com/photo-1476067897447-d0c5df27b5df?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTB8fHJvYWR8ZW58MHx8MHx8fDA%3D&auto=format&fit=crop&w=500&q=60');
  "
>
  <div class="row row-cols-1 row-cols-md-2 row-cols-lg-4">
    <div
      class="col d-flex flex-column align-items-center justify-content-center mb-2 mb-md-0"
    >
      <p class="fs-2 fw-bold mb-n1 colored-title">
        <ngx-animated-counter [params]="params[0]"></ngx-animated-counter>
      </p>
      <p>Active Students</p>
      <p class="heading-line rounded"></p>
    </div>
    <div
      class="col d-flex flex-column align-items-center justify-content-center mb-2 mb-md-0"
    >
      <p class="fs-2 fw-bold mb-n1 colored-title">
        <ngx-animated-counter [params]="params[1]"></ngx-animated-counter>
      </p>
      <p>Licenses Issued</p>
      <p class="heading-line rounded"></p>
    </div>
    <div
      class="col d-flex flex-column align-items-center justify-content-center mb-2 mb-md-0"
    >
      <p class="fs-2 fw-bold mb-n1 colored-title">
        <ngx-animated-counter [params]="params[2]"></ngx-animated-counter>
      </p>
      <p>Course Reviews</p>
      <p class="heading-line rounded"></p>
    </div>
    <div
      class="col d-flex flex-column align-items-center justify-content-center mb-2 mb-md-0"
    >
      <p class="fs-2 fw-bold mb-n1 colored-title">
        <ngx-animated-counter [params]="params[3]"></ngx-animated-counter>
      </p>
      <p>Years on the Market</p>
      <p class="heading-line rounded"></p>
    </div>
  </div>
</div>
<!-- Jumbotron -->
