import { Component, Input } from '@angular/core';
import { MdbModalRef } from 'mdb-angular-ui-kit/modal';

@Component({
  selector: 'app-confirmation-modal',
  templateUrl: './confirmation-modal.component.html',
  styleUrls: ['./confirmation-modal.component.scss'],
})
export class ConfirmationModalComponent {
  title: string;
  message: string;
  action: string;

  constructor(public modalRef: MdbModalRef<ConfirmationModalComponent>) {}

  //close the modal
  //by passing value called 'confirmed'
  //to the component that called the modal
  confirmed() {
    this.modalRef.close('confirmed');
  }
}
<div class="modal-header">
  <i class="far fa-trash-can me-2 fs-2 text-danger"></i>
  <h5 class="modal-title" id="exampleModalLabel">{{ title }}</h5>
  <button
    type="button"
    class="btn-close"
    aria-label="Close"
    (click)="modalRef.close()"
  ></button>
</div>
<div class="modal-body">
  {{ message }}
</div>
<div class="modal-footer bg-light">
  <button
    type="button"
    class="btn btn-outline-secondary text-dark"
    (click)="modalRef.close()"
  >
    Cancel
  </button>
  <button (click)="confirmed()" type="button" class="btn btn-danger">
    {{ action }}
  </button>
</div>
