import { Component, OnInit, Signal, computed } from '@angular/core';
import { UsersService } from '../users.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { User } from '../user.model';
import { ActivatedRoute } from '@angular/router';
import { FileService } from '../../files/file.service';

@Component({
  selector: 'app-users-account-edit',
  templateUrl: './users-account-edit.component.html',
  styleUrls: ['./users-account-edit.component.scss'],
})
export class UsersAccountEditComponent implements OnInit {
  userFormGroup: FormGroup;
  userId: string;
  constructor(
    private userService: UsersService,
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private fileService: FileService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.userId = params['id'];

      this.userFormGroup = this.formBuilder.group({
        name: ['', Validators.required],
        email: ['', [Validators.required, Validators.email]],
      });

      //file the form with the user details
      this.userService.getUser(this.userId).subscribe((user: User) => {
        this.userFormGroup.patchValue({
          name: user.name,
          email: user.email,
        });
      });
    });
  }

  user: Signal<User> = computed(() => this.userService.user());

  //show a loader during an HTTP POST OR UPDATE request
  isProcessingRequest: Signal<boolean> = computed(() =>
    this.userService.isProcessingRequest()
  );

  placeholderImageUrl: Signal<string> = computed(() =>
    this.userService.imagePlaceholderUrl(this.user().name)
  );
  //checking if the image file is valid
  isFileInvalid: Signal<boolean> = computed(() =>
    this.fileService.isFileInvalid()
  );

  submitForm(event: Event) {
    event.preventDefault();
    if (this.userFormGroup.valid) {
      let newUser = new User();

      newUser.name = this.userFormGroup.controls['name'].value;
      newUser.email = this.userFormGroup.controls['email'].value;
      newUser.imageUrl = this.user().imageUrl;
      this.userService.updateUser(this.userId, newUser);
    }
  }
}
<div class="container-fluid custom row row-cols-1 mt-5">
  <p class="heading-line m-auto rounded"></p>
  <p class="display-6 text-uppercase text-center">Update Profile</p>
  <form class="container col-lg-6" [formGroup]="userFormGroup">
    <!-- Image file upload start -->
    <div class="mb-4">
      <label for="course-image">Photo:</label>
      <app-file-image-item
        [editMode]="true"
        [imageName]="user().name"
        [currentImageUrl]="
          user().imageUrl ? user().imageUrl : placeholderImageUrl()
        "
      ></app-file-image-item>
    </div>
    <!-- Image file upload end -->
    <div class="mb-3">
      <label for="name">Name:</label>
      <input
        [ngClass]="{
          'is-invalid':
            userFormGroup.get('name').invalid &&
            userFormGroup.get('name').touched,
          'is-valid':
            userFormGroup.get('name').valid && userFormGroup.get('name').touched
        }"
        class="form-control"
        type="text"
        id="name"
        formControlName="name"
      />
      <div
        class="invalid-feedback"
        *ngIf="
          userFormGroup.get('name').invalid && userFormGroup.get('name').touched
        "
      >
        Required
      </div>
    </div>
    <div class="mb-3">
      <label for="email">Email:</label>
      <input
        [ngClass]="{
          'is-invalid':
            userFormGroup.get('email').invalid &&
            userFormGroup.get('email').touched,
          'is-valid':
            userFormGroup.get('email').valid &&
            userFormGroup.get('email').touched
        }"
        class="form-control"
        type="text"
        id="email"
        formControlName="email"
      />
      <div
        class="invalid-feedback"
        *ngIf="
          userFormGroup.get('email').invalid &&
          userFormGroup.get('email').touched
        "
      >
        Please enter a valid email address
      </div>
    </div>

    <!-- Submit button -->
    <button
      [ngClass]="{ disabled: userFormGroup.invalid || isFileInvalid() }"
      *ngIf="!isProcessingRequest(); else loadingBtn"
      (click)="submitForm($event)"
      type="submit"
      class="btn custom text-light btn-block mb-3"
    >
      Update
    </button>
    <!-- Loading button -->
    <ng-template #loadingBtn>
      <button class="btn custom text-light btn-block" type="button" disabled>
        <span
          class="spinner-border spinner-border-sm"
          role="status"
          aria-hidden="true"
        ></span>
        <span class="ms-1">Please wait...</span>
      </button>
    </ng-template>
  </form>
</div>
