import { Component, OnInit, Signal, computed } from '@angular/core';

import { CoursesService } from '../courses.service';
import { Course } from '../course.model';
import { UsersService } from 'src/app/users/users.service';
import { User } from 'src/app/users/user.model';
import { AppService } from 'src/app/app.service';

@Component({
  selector: 'app-course-list',
  templateUrl: './course-list.component.html',
  styleUrls: ['./course-list.component.scss'],
})
export class CourseListComponent implements OnInit {
  placeholderCards: number[] = [0, 1, 2];

  constructor(
    private courseService: CoursesService,
    private userService: UsersService,
    private appService: AppService
  ) {}

  ngOnInit() {
    //if there are no current courses in the store
    if (this.courseService.courseListSignal().length == 0) {
      this.courseService.getCourses();
    }
  }

  courses: Signal<Course[]> = computed(() =>
    this.courseService.courseListSignal()
  );

  //information about the current logged in user
  currentUser: Signal<User> = computed(() => this.userService.user());

  //display placeholder courses
  //in case its fetching courses
  isFetchingCourses: Signal<boolean> = computed(() =>
    this.courseService.isFetchingCourses()
  );
  success() {
    this.appService.showSuccessToast('Course has been updated', '');
  }
  failure() {
    this.appService.showFailureToast('Course has been updated', 'Failure');
  }
}
<div class="container-fluid m-auto mt-5">
  <div class="container m-auto text-center">
    <p class="heading-line m-auto rounded"></p>
    <p class="display-6 text-uppercase">Our Courses</p>
    <p>
      Explore our wide array of driving courses, catering to every stage of your
      journey on the road. From getting your learners license to mastering
      advanced driving techniques, we have the right course for you. Our
      offerings are designed to make your learning experience straightforward
      and effective, ensuring you're well-equipped for safe and confident
      driving.
    </p>
  </div>

  <!-- Add new course -->
  <div *ngIf="currentUser().isAdmin" class="d-flex justify-content-end mb-4">
    <a routerLink="new" class="btn btn-primary">Add new</a>
  </div>

  <app-course-filter></app-course-filter>

  <!-- Placeholder courses -->
  <div
    *ngIf="isFetchingCourses(); else elseBlock"
    class="mt-3 row row-cols-1 row-cols-md-3 g-4"
  >
    <div *ngFor="let item of placeholderCards" class="col">
      <div class="card">
        <img
          src="../../../assets/images/placeholder/placeholder1.png"
          class="card-img-top img-fluid"
          alt="placeholder image"
        />
        <div class="card-body">
          <h5 class="card-title placeholder-wave">
            <span class="placeholder col-6"></span>
          </h5>
          <p class="card-text placeholder-wave">
            <span class="placeholder col-7"></span>
            <span class="placeholder col-4"></span>
            <span class="placeholder col-4"></span>
            <span class="placeholder col-6"></span>
            <span class="placeholder col-8"></span>
          </p>
        </div>
      </div>
    </div>
  </div>
  <ng-template #elseBlock>
    <!-- Courses list -->
    <div class="row row-cols-1 row-cols-md-3 g-4 mt-3">
      <div class="col" *ngFor="let course of courses()">
        <app-course-item [course-detail]="course"></app-course-item>
      </div>
    </div>
  </ng-template>
</div>
