import { Component, Signal, computed } from '@angular/core';
import { GalleryService } from '../gallery.service';
import { FileService } from 'src/app/files/file.service';

@Component({
  selector: 'app-gallery-add',
  templateUrl: './gallery-add.component.html',
  styleUrls: ['./gallery-add.component.scss'],
})
export class GalleryAddComponent {
  constructor(
    private galleryService: GalleryService,
    private fileService: FileService
  ) {}

  addImage() {
    this.galleryService.uploadGalleryItem();
  }

  isUploadingItem: Signal<boolean> = computed(() =>
    this.galleryService.isUploadingItemSignal()
  );

  isFileInvalid: Signal<boolean> = computed(() =>
    this.fileService.isFileInvalid()
  );
}
<!-- Add Images Tab Start  -->
<div>
  <app-file-image-item [imageName]="'img'"></app-file-image-item>
  <div class="btn-container d-flex justify-content-center">
    <button
      *ngIf="!isUploadingItem(); else showLoader"
      [ngClass]="{ disabled: isFileInvalid() }"
      (click)="addImage()"
      class="btn custom btn-lg text-light"
    >
      Add image
    </button>
    <!-- Show a loader during a CRUD REQUEST -->
    <ng-template #showLoader>
      <button class="btn custom btn-lg text-light" type="button" disabled>
        <span
          class="spinner-border spinner-border-sm"
          role="status"
          aria-hidden="true"
        ></span>
        <span class="ms-1">Uploading image...</span>
      </button>
    </ng-template>
  </div>
</div>
<!-- Add Images Tab End  -->
