import { Component, Signal, computed } from '@angular/core';
import { Review } from '../../review.model';
import { ReviewsService } from '../../reviews.service';
import { Router } from '@angular/router';
import { MetaData } from '../../../app.meta';

@Component({
  selector: 'app-review-pagination',
  templateUrl: './review-pagination.component.html',
  styleUrls: ['./review-pagination.component.scss'],
})
export class ReviewPaginationComponent {
  maxSize: number = 10;
  constructor(private reviewService: ReviewsService, private router: Router) {}

  //Reviews meta data for pagination
  metaData: Signal<MetaData> = computed(() =>
    this.reviewService.metaDataSignal()
  );
  reviews: Signal<Review[]> = computed(() =>
    this.reviewService.reviewListSignal()
  );

  //Change reviews pagination number
  pageChanged(newPage) {
    let courseId = this.reviewService.courseIdSignal();
    //get reviews for the new page
    this.reviewService.getReviewsForCourse(courseId, newPage);

    //scroll up to the top of the review list
    const element = document.getElementById('reviews');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }
}
<!-- Pagination information Start -->
<div
  class="d-flex flex-column align-items-start flex-md-row align-items-md-center justify-content-md-between flex-wrap"
>
  <p [innerHTML]="metaData().printPaginationInfo('Reviews')"></p>
  <pagination-controls
    class="pagination-controls top-pagination-controls"
    [maxSize]="maxSize"
    (pageChange)="pageChanged($event)"
  ></pagination-controls>
</div>
<!-- Pagination information End-->
<app-review-item
  *ngFor="
    let review of reviews()
      | paginate
        : {
            itemsPerPage: metaData().pageSize,
            currentPage: metaData().currentPage,
            totalItems: metaData().totalItems
          }
  "
  [review-details]="review"
></app-review-item>

<!-- Bottom pagination controls start-->
<div class="d-flex justify-content-start mt-3">
  <pagination-controls
    class="pagination-controls bottom-pagination-controls"
    [maxSize]="maxSize"
    (pageChange)="pageChanged($event)"
  ></pagination-controls>
</div>
<!-- Bottom pagination controls end-->
